mapper.delete:
delete from tbl_pollstatus
where matchNo = #{matchNo}
	and pollType = #{pollType}

mapper.select:
select /*+ INDEX(tbl_pollparticipate idx_pp) */ * 
from tbl_pollparticipate
where clubCode = #{clubCode}

select /*+ INDEX(tbl_pollparticipate idx_pp) */ * 
from tbl_pollparticipate
where clubCode = #{clubCode}
	and matchNo = #{matchNo}

select * 
from tbl_pollstatus
where matchNo = #{matchNo}
	and pollType = #{pollType}

mapper.update:
update tbl_pollstatus 
set status = ${status}
where matchNo = #{matchNo}
	and userId = #{userId}
	and pollType = #{pollType}

update tbl_pollstatus 
set picked = picked+1
where matchNo = #{matchNo}
	and userId = #{userId}
	and pollType = #{pollType}

update tbl_pollparticipate 
set endDate = #{endDate}
where matchNo = #{matchNo}

mapper.select:
select /*+ INDEX(tbl_pollmom idx_pm) */ * 
from tbl_pollmom
where clubCode = #{clubCode}

mapper.update:
update tbl_pollmom 
set endDate = #{endDate}
where matchNo = #{matchNo}
