mapper.select:
select /*+ INDEX(tbl_pollparticipate idx_pp) */ * 
from tbl_pollparticipate
where clubCode = #{clubCode}

select /*+ INDEX(tbl_pollparticipate idx_pp) */ * 
from tbl_pollparticipate
where clubCode = #{clubCode}
	and matchNo = #{matchNo}

mapper.update:
update tbl_pollstatus 
set status = ${status}
where matchNo = #{matchNo}
	and userId = #{userId}

update tbl_pollparticipate 
set endDate = #{endDate}
where matchNo = #{matchNo}
