mapper.update:
update tbl_freeboard 
set boardHit = boardHit + 1
where boardNo = #{boardNo}

update tbl_freeboard 
set replyCnt = replyCnt + 1
where boardNo = #{boardNo}

update tbl_freeboard 
set boardTitle = #{boardTitle}
, boardTop = #{boardTop}
, boardContent = #{boardContent}
, boardDate = sysdate
where boardNo = #{boardNo}
