mapper.select:
select /*+ INDEX(tbl_matches idx_matches) */ *
from tbl_matches
where clubCode = #{clubCode}

select /*+ INDEX(tbl_matches idx_matches) */ *
from tbl_matches
where matchNo = #{matchNo}

mapper.update:
update tbl_matches
set apposingTeam = #{apposingTeam}
, matchDate = #{matchDate}
, stadium = #{stadium}
, matchStatus = #{matchStatus}
